package javanav;

public class LoopsConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// While loop
		// Disadvantages of while loop: It will generate infinite loop if u dont give incremental/decremenatal
		
		int i=1; //INitilization
		while(i<=10) // Condition
		{
			System.out.println(i);
			i++; // Increment
		}
		
		System.out.println("***********************");
		
		// For loop
		
		for(int j=10; j<=20; j++)  // INitilization, Conditional, Increment/Decrement
		{
			System.out.println(j);
		}
		
		System.out.println("******************************");
		
		for(int k=10; k>=1; k--)  // INitilization, Conditional, Increment/Decrement
		{
			System.out.println(k);
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
